contracts/TrailMix.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;
pragma abicoder v2;

import { AutomationCompatibleInterface } from "@chainlink/contracts/src/v0.8/automation/interfaces/AutomationCompatibleInterface.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import { ISwapRouter } from "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
// import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { IUniswapOracle } from "./IUniswapOracle.sol";

error InvalidAmount(); // Error for when the deposit amount is not positive
error TransferFailed(); // Error for when the token transfer fails
error InvalidToken(); // Error for when the token address is invalid

contract TrailMix is ReentrancyGuard {
	address private immutable i_manager; //address of the manager contract
	address private immutable i_creator; // address of the creator of the contract

	address private s_erc20Token;
	address private s_stablecoin;

	ISwapRouter private s_uniswapRouter;
	address public immutable s_uniswapPool;
	IUniswapOracle private s_uniswapOracle; // TWAP oracle for Uniswap V3

	uint256 private immutable s_trailAmount; // Amount to trail by as a %
	uint256 private s_tslThreshold; // User's TSL threshold
	uint256 private s_erc20Balance;
	uint256 private s_stablecoinBalance; // User's ERC20 token balance
	uint256 private s_granularity; //  % price increase to trigger an update
	bool private s_isTSLActive; // Indicates if the TSL is currently active
	bool private slippageProtection; // Indicates if slippage protection is enabled
	uint24 private s_poolFee;

	//events are emitted in the manager contract

	constructor(
		address _manager,
		address _creator,
		address _erc20Token,
		address _stablecoin,
		address _uniswapRouter,
		address _uniswapPool,
		address _uniswapOracle,
		uint256 _trailAmount,
		uint256 granularity,
		uint24 _poolFee
	) {
		i_manager = _manager;
		i_creator = _creator;

		s_erc20Token = _erc20Token;
		s_stablecoin = _stablecoin;

		s_uniswapRouter = ISwapRouter(_uniswapRouter);
		s_uniswapOracle = IUniswapOracle(_uniswapOracle);
		s_uniswapPool = _uniswapPool;

		s_isTSLActive = false;
		s_trailAmount = _trailAmount;
		slippageProtection = true;
		s_granularity = granularity;
		s_poolFee = _poolFee;
	}

	modifier onlyManager() {
		require(msg.sender == i_manager, "only callable by manager contract");
		_;
	}

	/**
	 * @notice Deposits a specified amount of the ERC20 token into the contract.
	 * @param amount The amount of the ERC20 token to deposit.
	 * @param tslThreshold The initial trailing stop loss threshold as a percentage.
	 */
	function deposit(
		uint256 amount,
		uint256 tslThreshold
	) external onlyManager {
		if (amount <= 0) {
			revert InvalidAmount();
		}

		bool transferSuccess = IERC20(s_erc20Token).transferFrom(
			i_manager,
			address(this),
			amount
		);
		if (!transferSuccess) {
			revert TransferFailed();
		}

		s_erc20Balance += amount;

		if (!s_isTSLActive) {
			// If TSL is not active, set the threshold and activate TSL
			s_tslThreshold = (tslThreshold * (100 - s_trailAmount)) / 100;
			s_isTSLActive = true;
		}
	}

	/**
	 * @notice Withdraws the user's funds from the contract.
	 * @dev Allows withdrawal of either ERC20 tokens or stablecoins, based on TSL status.
	 */
	function withdraw(address token) external onlyManager {
		uint256 withdrawalAmount;

		if (token == s_stablecoin) {
			// If TSL is not active, assume user wants to withdraw stablecoins
			// Logic to handle stablecoin withdrawal
			withdrawalAmount = s_stablecoinBalance;
			if (withdrawalAmount <= 0) {
				revert InvalidAmount();
			}
			s_stablecoinBalance = 0;
			TransferHelper.safeTransfer(
				s_stablecoin,
				i_creator, // sends funds to the contract creator
				withdrawalAmount
			);
			s_isTSLActive = false; // Deactivate TSL when withdrawal is made
		} else if (token == s_erc20Token) {
			// If TSL is active, user withdraws their ERC20 tokens
			withdrawalAmount = s_erc20Balance;
			if (withdrawalAmount <= 0) {
				revert InvalidAmount();
			}
			s_erc20Balance = 0;
			TransferHelper.safeTransfer(
				s_erc20Token,
				i_creator,
				withdrawalAmount
			);
			s_isTSLActive = false; // Deactivate TSL when withdrawal is made
		} else {
			revert InvalidToken();
		}
	}

	/**
	 * @notice Checks if upkeep is needed based on TSL conditions.
	 * @dev Part of the Chainlink automation interface.
	 * @return A tuple of three values: a boolean indicating if selling is needed, a boolean indicating if the threshold should be updated, and the new threshold value.
	 */
	function checkUpkeepNeeded() external view returns (bool, bool, uint256) {
		// Implement logic to check if TSL conditions are met
		uint256 currentPrice = getTwapPrice();
		uint256 exactPrice = getExactPrice();

		bool triggerSell = false;
		bool updateThreshold = false;
		uint256 newThreshold = 0;

		// Calculate 10% price range bounds
		uint256 lowerBound = (currentPrice * 90) / 100;
		uint256 upperBound = (currentPrice * 110) / 100;
		//calculates the old all time high price based on the threshold
		uint256 oldCurrentPrice = (s_tslThreshold * 100) /
			(100 - s_trailAmount);

		//determines the price that is granularity% higher than the old stored price
		uint256 minPriceForUpdate = (oldCurrentPrice * (100 + s_granularity)) /
			100;
		//if new price is less than the current threshold then trigger TSL
		if (exactPrice >= lowerBound && exactPrice <= upperBound) {
			if (currentPrice < s_tslThreshold) {
				//trigger TSL
				triggerSell = true;
			} else if (currentPrice > minPriceForUpdate) {
				updateThreshold = true;
				newThreshold = (currentPrice * (100 - s_trailAmount)) / 100;
			}
		}
		return (triggerSell, updateThreshold, newThreshold);
	}

	/**
	 * @notice Updates the trailing stop loss threshold.
	 * @dev This function is private and should be called only by performUpkeep.
	 * @param newThreshold The new threshold value to set.
	 */
	function updateTSLThreshold(uint256 newThreshold) external onlyManager {
		s_tslThreshold = newThreshold;
	}

	/**
	 * @notice Gets the latest price of the ERC20 token in stablecoins.
	 * @dev Uses the Uniswap Oracle to get the latest price using TWAP (time-weighted average price) data for the past 5 minutes
	 * @return The latest price of the ERC20 token in stablecoins.
	 */
	function getTwapPrice() public view returns (uint256) {
		uint256 amountOut = s_uniswapOracle.estimateAmountOut(
			s_uniswapPool,
			s_erc20Token,
			1e18, // number of decimals for erc20 token
			300 // 5 minutes of price data (300 seconds)
		);
		return amountOut;
	}

	function getExactPrice() public view returns (uint256) {
		uint256 amountOut = s_uniswapOracle.estimateAmountOut(
			s_uniswapPool,
			s_erc20Token,
			1e18, // number of decimals for erc20 token
			1
		);
		return amountOut;
	}

	/**
	 * @notice Swaps the user's ERC20 tokens for stablecoins on Uniswap.
	 * @dev Currently public for testing, but intended to be private in deployment. Non-reentrant.
	 * @param amount The amount of the ERC20 token to swap.
	 */
	function swapOnUniswap(uint256 amount) public nonReentrant {
		//swap ERC20 tokens for stablecoin on uniswap
		//need to approve uniswap to spend ERC20 tokens

		//gets the most up to date price to calculate slippage
		uint256 currentPrice = getExactPrice();
		uint256 minAmountOut;
		if (slippageProtection) {
			minAmountOut = (amount * currentPrice * 995) / (1000 * 1e18); //99.5% of the current price
		} else {
			minAmountOut = 0;
		}

		IERC20(s_erc20Token).approve(address(s_uniswapRouter), amount);

		s_erc20Balance -= amount;
		ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
			.ExactInputSingleParams({
				tokenIn: s_erc20Token,
				tokenOut: s_stablecoin,
				fee: s_poolFee,
				recipient: address(this),
				deadline: block.timestamp,
				amountIn: amount,
				amountOutMinimum: minAmountOut,
				sqrtPriceLimitX96: 0
			});
		s_uniswapRouter.exactInputSingle(params);

		uint256 amountRecieved = IERC20(s_stablecoin).balanceOf(address(this));
		s_stablecoinBalance += amountRecieved;
	}

	/**
	 * @notice Activates slippage protection for token swaps.
	 * @dev Can only be called by the contract owner.
	 */
	function toggleSlippageProtection() public onlyManager {
		slippageProtection = !slippageProtection;
	}

	// View functions for contract interaction and frontend integration
	function getERC20Balance() public view returns (uint256) {
		return s_erc20Balance;
	}

	function getStablecoinBalance() public view returns (uint256) {
		return s_stablecoinBalance;
	}

	function getTSLThreshold() public view returns (uint256) {
		return s_tslThreshold;
	}

	function isTSLActive() public view returns (bool) {
		return s_isTSLActive;
	}

	// View function to get ERC20 token address
	function getERC20TokenAddress() public view returns (address) {
		return s_erc20Token;
	}

	// View function to get stablecoin address
	function getStablecoinAddress() public view returns (address) {
		return s_stablecoin;
	}

	// View function to get Uniswap router address
	function getUniswapRouterAddress() public view returns (address) {
		return address(s_uniswapRouter);
	}

	function getTrailAmount() public view returns (uint256) {
		return s_trailAmount;
	}

	function getManager() public view returns (address) {
		return i_manager;
	}

	function getCreator() public view returns (address) {
		return i_creator;
	}

	function getGranularity() public view returns (uint256) {
		return s_granularity;
	}

	function getUniswapPool() public view returns (address) {
		return s_uniswapPool;
	}
}

contracts/TrailMixManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import { AutomationCompatibleInterface } from "@chainlink/contracts/src/v0.8/automation/interfaces/AutomationCompatibleInterface.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
// import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";

error NotContractOwner(); // Error for when the caller is not the contract owner

import { TrailMix } from "./TrailMix.sol"; // Import TrailMix contract
import { ITrailMix } from "./ITrailMix.sol"; // Import ITrailMix interface

contract TrailMixManager is AutomationCompatibleInterface, ReentrancyGuard {
	//array storing all active strategies
	address[] public activeStrategies;
	// mapping to store index of strategy in activeStrategies array
	mapping(address => uint256) private strategyIndex;

	//mapping for quick lookup for if a strategy is active
	mapping(address => bool) public isActiveStrategy;

	// Mapping from user address to array of deployed TrailMix contract addresses
	mapping(address => address[]) public userContracts;

	// Event to emit when a new TrailMix contract is deployed
	event ContractDeployed(
		address indexed creator,
		address contractAddress,
		uint256 timestamp
	);

	event FundsDeposited(
		address indexed creator,
		address indexed strategy,
		uint256 depositPrice,
		uint256 amount,
		address token,
		uint256 timestamp
	);
	event FundsWithdrawn(
		address indexed creator,
		address indexed strategy,
		uint256 amount,
		address token,
		uint256 timestamp
	);
	event ThresholdUpdated(
		address indexed strategy,
		uint256 oldThreshold,
		uint256 newThreshold,
		uint256 timestamp
	);
	event SwapExecuted(
		address indexed strategy,
		address indexed creator,
		uint256 amountIn,
		uint256 amountOut,
		address tokenIn,
		address tokenOut,
		uint256 timestamp
	);

	// Function to deploy a new TrailMix contract
	function deployTrailMix(
		address _erc20Token,
		address _stablecoin,
		address _uniswapRouter,
		address _uniswapPool,
		address _uniswapOracle,
		uint256 _trailAmount,
		uint256 _granularity,
		uint24 _poolFee
	) public {
		// Deploy the TrailMix contract
		TrailMix newTrailMix = new TrailMix(
			address(this), // The TrailMixManager contract address
			msg.sender, // The user becomes the creator of the TrailMix contract
			_erc20Token,
			_stablecoin,
			_uniswapRouter,
			_uniswapPool,
			_uniswapOracle,
			_trailAmount,
			_granularity,
			_poolFee
		);

		// Store the contract address in the userContracts mapping
		userContracts[msg.sender].push(address(newTrailMix));
		activeStrategies.push(address(newTrailMix));
		isActiveStrategy[address(newTrailMix)] = true;
		strategyIndex[address(newTrailMix)] = activeStrategies.length - 1;

		// Emit an event for the deployment
		emit ContractDeployed(
			msg.sender,
			address(newTrailMix),
			block.timestamp
		);
	}

	// IMPLEMENT DEPOSIT AND WITHDRAW FUNCTIONS
	function deposit(
		address _strategy,
		uint256 _amount,
		uint256 _tslThreshold
	) public {
		if (ITrailMix(_strategy).getCreator() != msg.sender) {
			revert NotContractOwner();
		}

		// Get the ERC20 token address from the TrailMix contract
		address erc20TokenAddress = ITrailMix(_strategy).getERC20TokenAddress();
		//transfer funds from user to the manager contract
		IERC20(erc20TokenAddress).transferFrom(
			msg.sender,
			address(this),
			_amount
		);

		// approve strategy to spend the funds and call deposit
		IERC20(erc20TokenAddress).approve(_strategy, _amount);
		ITrailMix(_strategy).deposit(_amount, _tslThreshold);

		// Emit an event for the deposit
		emit FundsDeposited(
			msg.sender,
			_strategy,
			ITrailMix(_strategy).getExactPrice(),
			_amount,
			ITrailMix(_strategy).getERC20TokenAddress(),
			block.timestamp
		);
	}

	function withdraw(address _strategy, address _token) public nonReentrant {
		// Withdraw the user's funds from the TrailMix contract
		if (ITrailMix(_strategy).getCreator() != msg.sender) {
			revert NotContractOwner();
		}
		ITrailMix(_strategy).withdraw(_token);
		removeStrategy(_strategy);
	}

	function toggleSlippageProtection(address _strategy) public {
		if (ITrailMix(_strategy).getCreator() != msg.sender) {
			revert NotContractOwner();
		}
		ITrailMix(_strategy).toggleSlippageProtection();
	}

	// Remove a strategy
	function removeStrategy(address strategy) private {
		require(strategy != address(0), "Invalid address");
		require(isActiveStrategy[strategy], "Strategy not active");

		isActiveStrategy[strategy] = false;

		// Move the last element into the place to delete
		uint256 index = strategyIndex[strategy];
		address lastStrategy = activeStrategies[activeStrategies.length - 1];
		activeStrategies[index] = lastStrategy;
		strategyIndex[lastStrategy] = index;
		activeStrategies.pop();

		// Clean up
		delete strategyIndex[strategy];
	}

	/**
	 * @notice Checks if upkeep is needed based on TSL conditions.COMPUTED OFF-CHAIN
	 * @dev Part of the Chainlink automation interface.
	 * @param 'checkData' Not used in this implementation.
	 * @return upkeepNeeded Boolean flag indicating if upkeep is needed.
	 * @return performData Encoded data on what action to perform during upkeep.
	 */
	function checkUpkeep(
		bytes calldata /*checkData*/
	) external view returns (bool upkeepNeeded, bytes memory performData) {
		for (uint256 i = 0; i < activeStrategies.length; i++) {
			(bool sell, bool update, uint256 newThreshold) = ITrailMix(
				activeStrategies[i]
			).checkUpkeepNeeded();

			if (sell) {
				// Prioritize swap action if needed
				performData = abi.encode(
					activeStrategies[i],
					sell,
					update,
					newThreshold
				);
				return (true, performData);
			} else if (update) {
				// If no swap needed, check for threshold update
				// Note: This approach only encodes action for the first strategy needing an action.
				// If you want to encode actions for all strategies, you'd need to aggregate the data differently.
				performData = abi.encode(
					activeStrategies[i],
					sell,
					update,
					newThreshold
				);
				// Don't return yet if you want to prioritize sells across all strategies
			}
		}

		upkeepNeeded = (performData.length > 0);
		// performData already set within the loop for the first action identified
		return (upkeepNeeded, performData);
	}

	/**
	 * @notice Performs the upkeep of updating the stop loss threshold or triggering a sell.
	 * @dev Part of the Chainlink automation interface.
	 * @param performData Encoded data indicating the actions to perform.
	 */
	function performUpkeep(bytes calldata performData) external override {
		// Implement logic to perform TSL (e.g., swap to stablecoin) when conditions are met
		(
			address strategy,
			bool sell,
			bool updateThreshold,
			uint256 newThreshold
		) = abi.decode(performData, (address, bool, bool, uint256));
		if (sell) {
			//call trigger function to sell on uniswap
			uint256 s_erc20Balance = ITrailMix(strategy).getERC20Balance();
			ITrailMix(strategy).swapOnUniswap(s_erc20Balance);
			//deactivate TSL
			removeStrategy(strategy);

			//emit swap event
			emit SwapExecuted(
				strategy,
				ITrailMix(strategy).getCreator(),
				s_erc20Balance,
				ITrailMix(strategy).getStablecoinBalance(),
				ITrailMix(strategy).getERC20TokenAddress(),
				ITrailMix(strategy).getStablecoinAddress(),
				block.timestamp
			);
		} else if (updateThreshold) {
			//call updateThreshold function to update the threshold
			ITrailMix(strategy).updateTSLThreshold(newThreshold);
			//emit event for threshold update
			emit ThresholdUpdated(
				strategy,
				ITrailMix(strategy).getTSLThreshold(),
				newThreshold,
				block.timestamp
			);
		}
	}

	// Function to get all contracts deployed by a user
	function getUserContracts(
		address user
	) public view returns (address[] memory) {
		return userContracts[user];
	}
}
